import React, { useState } from "react";

const AssignTaskModal = ({ isOpen, setIsOpen, taskId }) => {
  const [selectedMember, setSelectedMember] = useState("");

  const handleAssignTask = () => {
    // Add your logic to assign the task to the selected member
    console.log(`Assign task with ID ${taskId} to member ${selectedMember}`);
    // Close the modal
    setIsOpen(false);
  };

  return (
    <div className={`fixed inset-0 flex items-center justify-center z-50 ${isOpen ? "" : "hidden"}`}>
      <div className="bg-white rounded-lg shadow-lg p-6">
        <h2 className="text-xl font-semibold mb-4">Assign Task</h2>
        <label htmlFor="member" className="block mb-2">
          Select a member:
        </label>
        <select
          id="member"
          className="w-full p-2 border border-gray-300 rounded-md mb-4"
          value={selectedMember}
          onChange={(e) => setSelectedMember(e.target.value)}
        >
          <option value="">Select a member</option>
          <option value="member1">Member 1</option>
          <option value="member2">Member 2</option>
          {/* Add more member options if needed */}
        </select>
        <div className="flex justify-end">
          <button
            className="bg-gray-200 text-gray-700 px-4 py-2 rounded-md mr-2"
            onClick={() => setIsOpen(false)}
          >
            Cancel
          </button>
          <button
            className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-md"
            onClick={handleAssignTask}
          >
            Assign
          </button>
        </div>
      </div>
    </div>
  );
};

export default AssignTaskModal;
